// store.js
import { createStore } from 'vuex';

const store = createStore({
  state: {
    current_user:{},
    user_token:"" // This is where your data is stored
  },
  mutations: {
    ADD_USER(state, fruit) {
       // Mutation to add a fruit to the list
    },
  },
  actions: {
    addUser({ commit }, fruit) {
      DE
      commit('ADD_USER', fruit); // Action to call the mutation
    },
  },
  getters: {
    allFruits(state) {
      return state.fruits; // Getter to access the fruits
    },
  },
});

export default store;
