// store.js
import { createStore } from 'vuex';

const store = createStore({
  state: {
    current_user:{},
    user_token:"" // This is where your data is stored
  },
  mutations: {
    ADD_USER(state, user_data) {
      state.current_user = user_data,
      state.user // Mutation to add a fruit to the list
    },
  },
  actions: {
    addUser({ commit }, user_data) {
    debugger
    commit('ADD_USER', user_data); // Action to call the mutation
    },
  },
  getters: {
    allFruits(state) {
      return state.fruits; // Getter to access the fruits
    },
  },
});

export default store;
