import { createStore } from 'vuex';

export default createStore({
  state: {
    user: null, 
    isAuthenticated: false, 
    token: null,
  },
  mutations: {
    setUser(state, user) {
      state.user = user;
      state.isAuthenticated = true;
    },
    setToken(state, token) {
      state.token = token;
    },
    logout(state) {
      state.user = null;
      state.isAuthenticated = false;
      state.token = null;
    },
  },
  actions: {
    login({ commit }, user) {
      commit('setUser', user);
    },
    logout({ commit }) {
      commit('logout');
    },
    tokenLogin({ commit }, token) {
      commit('setToken', token);
    }
  },
  getters: {
    getUser(state) {
      return state.user;
    },
    isAuthenticated(state) {
      return state.isAuthenticated;
    },
    getToken(state) {
      return state.token;
    }
  },
});
