// store.js
import { createStore } from 'vuex';

const store = createStore({
  state: {
    current_user:{},
     // This is where your data is stored
  },
  mutations: {
    ADD_FRUIT(state, fruit) {
      state.fruits.push(fruit); // Mutation to add a fruit to the list
    },
  },
  actions: {
    addUser({ commit }, fruit) {
      commit('ADD_FRUIT', fruit); // Action to call the mutation
    },
  },
  getters: {
    allFruits(state) {
      return state.fruits; // Getter to access the fruits
    },
  },
});

export default store;
